import React, { useEffect } from 'react';
import { capitalizeFirstLetter } from '../../utils/helpers';

function Nav(props) {
  

//   useEffect(() => {
//     document.title = capitalizeFirstLetter(currentCategory.name);
//   }, [currentCategory]);

  return (
    <header className="flex-row px-1">
      <h2>
        <a data-testid="link" href="/">
          Elizabeth Sherrow - A Portfolio
        </a>
      </h2>
      <nav>
        <ul className="flex-row">
          <li className="mx-2">
            <a data-testid="about" href="#about" onClick={() => props.setContactSelected(false)}>
              About Me
            </a>
            {/* <a data-testid="portfolio" href="#portfolio" onClick={() => props.setContactSelected(false)}>
              Portfolio
            </a> */}
            <a data-testid="resume" href="#resume" onClick={() => props.setContactSelected(false)}>
              Resume
            </a>

          </li>
          <li className={`mx-2 ${props.portfolioSelected && 'navActive'}`}>
            <span onClick={() => props.setPortfolioSelected(true)}>Portfolio</span>
          </li>
          <li className={`mx-2 ${props.contactSelected && 'navActive'}`}>
            <span onClick={() => props.setContactSelected(true)}>Contact</span>
          </li>
          { props.categories.length>0 ?  props.categories.map((category) => (
            <li
              className={`mx-1 ${
                props.currentCategory.name === category.name && !props.contactSelected && 'navActive'
                }`}
              key={category.name}
            >
              <span
                onClick={() => {
                  props.setCurrentCategory(category);
                  props.setContactSelected(false);
                }}
              >
                {capitalizeFirstLetter(category.name)}
              </span>
            </li>
          ))  :""}
        </ul>
      </nav>
    </header>
  );
}

export default Nav;
