import React, {useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

const useStyles = makeStyles({
  root: {
    maxWidth: 345,
  },
  media: {
    height: 140,
  },
});

export default function MediaCard() {
  const classes = useStyles();
  const [currentProject, setCurrentProject]= useState();
  const [projects]=useState([
      {
        name:'Budget Tracker',
        repo:'https://github.com/esherrow/budgetr-tracker',
        site:'https://stark-bastion-00092.herokuapp.com/'
      },{
        name:'Just Music',
        repo:'https://github.com/dillin92/just-music',
        site:'https://justmusic-3.herokuapp.com/'  
      },{
        name:'Password Generator',
        repo:'https://github.com/esherrow/password-generator',
        site:'http://esherrow.github.io/password-generator/'  
      },{
        name:'Weather Dashboard',
        repo:'https://github.com/esherrow/weather-dash',
        site:'https://esherrow.github.io/weather-dash/'  
      },{
        name:'Th TECH Blog',
        repo:'https://github.com/esherrow/tech-blog',
        site:'https://enigmatic-bastion-80011.herokuapp.com/'  
      },{
        name:'Workday Scheduler',
        repo:'https://github.com/esherrow/workday-scheduler',
        site:'https://esherrow.github.io/workday-scheduler/'  
      }
  ]);

  return (
      <div className="col-sm-4 mb-5">
          {currentProject.map((image, i) => (
    <Card className={classes.root}>
      <CardActionArea>
        <CardMedia
          className={classes.media}
          image="/static/images/cards/contemplative-reptile.jpg"
          title="Contemplative Reptile"
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="h2">
            Lizard
          </Typography>
        </CardContent>
      </CardActionArea>
      <CardActions>
        <Button size="small" color="primary">
          See it in Action
        </Button>
        <Button size="small" color="primary">
          See the Code
        </Button>
      </CardActions>
    </Card>
          ))}
    </div>
  );
}
